import React,{useEffect,useState} from 'react';
import axios from 'axios';
import Multiselect from 'multiselect-react-dropdown';

function Schedule()
{
    const [arr, arrset] = useState([]);
    console.log("hbee");
    useEffect(() => {
        async function fetchapi()
        {
            try {
                const data = await axios.get("/user");
                console.log(data.data);
                arrset(data.data.data);
                } 
            catch (err) {
            console.log(err);
            }
        }
        fetchapi();
    }, [])
    return(
        <>
            <h1>Schedule An Interview</h1>
            <div className='App'></div>
            <h3>Select Canditates</h3>
            <Multiselect options={arr} displayValue={arr.name}/>
            <form method='POST'>
                <input type="text" name="name" placeholder="Enter Name" required/>
                <input type="email" name="email" placeholder="Enter Email" required/>
                <input type="text" name="role"  defaultValue={'User'}/>
                <select multiple={true} value={arr && arr?.map((ele, key) =>
                <p>{ele.email}</p>
            )}></select>
            </form>
            <div className='few'>
            </div>
        </>


    )
}

export default Schedule
