import React,{useEffect,useState} from 'react';
import axios from 'axios';
import Multiselect from 'multiselect-react-dropdown';


function Schedule()
{
    const [arr, arrset] = useState([]);
    const [dat,datset]=useState([{'name':'','email':''}]);
    console.log("hbee");
    useEffect(() => {
        async function fetchapi()
        {
            try {
                const data = await axios.get("/user");
                console.log(data.data)
                arrset(data.data.data);
                datset(data.data.data);
                } 
            catch (err) {
            console.log(err);
            }
        }
        fetchapi();
    }, [])
    console.log(dat);
    return(
        <>
            <h4 style={{margin:"auto"}}>Schedule An Interview</h4>
            <div style={{margin:"auto"}}>
            <form>
                <label style={{fontSize:"10px"}}>Choose Date:</label>
                <input type="date" ></input>
                <label>Start time:</label>
                <input type="time" ></input>
                <label>End time:</label>
                <input type="time" ></input>
                <label>Select Canditates</label>
                <select required={true}>
                    <option selected disabled hidden>Select an option</option>
                    {dat && dat.map((ele)=>
                    <option>{ele.name}</option>
                    )}
                </select>
                <label>Select Interviewer</label>
                <select required={true}>
                    <option selected disabled hidden>Select an option</option>
                    {dat && dat.map((ele)=>
                    <option>{ele.name}</option>
                    )}
                </select>
                <button>Schedule Interview</button>
            </form>
            </div>
        </>


    )
}

export default Schedule
